export interface QtumRUTXO {
    address: string;
    txid: string;
    hash: string;
    pos: number;
    /**
     * Public key that controls this UXTO, as hex string.
     */
    scriptPubKey: string;
    amount: number;
    value: number;
    isStake: boolean;
    confirmations: number;
}
export interface QtumIUTXO {
    address: string;
    txid: string;
    vout: number;
    /**
     * Public key that controls this UXTO, as hex string.
     */
    scriptPubKey: string;
    amount: number;
    satoshis: number;
    isStake: boolean;
    height: number;
    confirmations: number;
}
